mapper.select:
select product_id, product_name, price, quantity, imagename, category_id
from product
where product_id = #{product_id}

mapper.update:
update product
set product_name=#{product_name},
    price=#{price},
    quantity=#{quantity},
    category_id=#{category_id}
where product_id = #{product_id};

update product
set imagename=#{imagename}
where product_id = #{product_id};
